    # Define a function to get the embeddings for each sentence
    def get_sentence_embedding(sentence):
        words = sentence.split()
        vectors = []
        for word in words:
            try:
                vectors.append(glove_model[word])
            except KeyError:
                pass
        if len(vectors) > 0:
            return np.mean(vectors, axis=0)
